INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (1517,'PF159529','분장실','2020.01.10','2020.02.09','하마씨어터(구. 가든씨어터)','http://www.kopis.or.kr/upload/pfmPoster/PF_PF159529_191211_113926.gif','공연완료','N','FC001232',NULL,TO_CLOB('SYNOPSIS"기억을지워드립니다”를공연하는5명의배우들.공연이시작되고무대위에서그리고분장실에서마저연기를이어가야하는배우들에게뜻하지않은사건들이발생한다.초대관객명단을듣고사색이되어버린아랑.아들밖에모르는아들바보공태에게걸려온전화.귀신을보는애림.주인공이하고싶은수인.운동밖에모르는성장.과연무사히공연을마칠수있을까'),NULL,NULL,NULL);
