INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (1598,'PF158574','오아시스세탁소습격사건','2019.12.05','2019.12.29','열린극장','http://www.kopis.or.kr/upload/pfmPoster/PF_PF158574_191122_153312.gif','공연완료','N','FC000516',NULL,TO_CLOB('줄거리오아시스세탁소의마음좋은주인장강태국의주변으로일상속에만나는사람들이꿈꿔왔던것을이루어주는보물이오아시스세탁소에숨겨져있다고믿게되는데과연그들이진심으로원하던보물이있었을까?유학가고,팔자고치고엄니호강시켜드리고..그러나그들의꿈에는다만때가끼었을뿐사악하지만은않다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (1628,'PF158288','고린내 [대전]','2019.11.14','2019.11.30','소극장 고도','http://www.kopis.or.kr/upload/pfmPoster/PF_PF158288_191118_135850.gif','공연완료','N','FC000623',NULL,TO_CLOB('줄거리]가진것하나없이몸뚱이로장사하는히빠리골목사람들이야기.아가씨장사를하면서도자긴히빠리사람들과다르다고생각하는포주한해숙.끌어오는손님보다벌금을더받는다는면박에직접박카스를팔러나가는환갑의펨푸정아수.하나있는자식앞에몸파는꼴을보이고도계속장사를해나가는이경하.지금은히빠리에이스지만세월앞에점점정아수나이경하처럼늙게될김미주.겨우히빠리골목을떠나나싶더니아예세상을떠나버린김숙이.그리고아가씨들에게약팔던시절을몸팔지않은것으로스스로대견해하며살았지만,결국박카스라도팔게되는최연히.위태위태한각자의삶을어떻게든붙잡고살아가는그들.재개발바람이불어닥쳐삶의터전까지위태로워진골목에서그들은서로에게기대살아간다'),NULL,NULL,NULL);
