INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (1908,'PF156095','제1회 말모이 연극제, 눈 오는 봄 날','2019.10.15','2019.10.20','대학로스타시티','http://www.kopis.or.kr/upload/pfmPoster/PF_PF156095_191014_155415.gif','공연완료','N','FC000389',NULL,TO_CLOB('줄거리치옥은20여년만에처녀때살았던고향을찾아온다.고향의이곳저곳을돌아보며옛적의추억을그려본다.치옥은기억속에살아있는옛추억들을하나하나떠울린다.술에취하면주사가심한아버지와너무순종적이어서답답한어머니.늘주위사람들을보살펴주며중심을잡아주는점빵아주머니와그의남편백수아저씨와''동갑친구이지만항상말썽만부리는그들의하나밖에없는자식.예쁜딸과같이살지만타지에서이사온무당이라늘눈치만보고사는아줌마.육지에서제주도로시집을왔지만도박에미쳐남편에게맞고사는이웃집언니등.이들에게이마을이재개발지역으로선정되었다는소식이들려오고,이웃들이하나둘씩보상금을받아떠나기시작한다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (1942,'PF155859','제14회 여성연출가전, 풍류극','2019.10.09','2019.10.13','선돌극장','http://www.kopis.or.kr/upload/pfmPoster/PF_PF155859_191010_094215.gif','공연완료','N','FC000362',NULL,TO_CLOB('줄거리정철은한때절친한이의제자였던정여립을직접만나기위해김장생을데리고진안으로내려온다.정여립은오랜만에만난정철을반갑게맞이하고술과풍류로대접을한다.술이한잔씩들어가면서양반들의연희도절정에이르며화기애애한분위기가고조된다'),NULL,NULL,NULL);
