INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2576,'PF151006','제17회 김천국제가족연극제, 두들팝','2019.07.24','2019.07.24','안산공원(천막극장)','http://www.kopis.or.kr/upload/pfmPoster/PF_PF151006_190704_105551.jpg','공연완료','N','FC002070',NULL,TO_CLOB('줄거리>호기심많은두친구가초대하는신나는낙서의세계까만멘만있으면무엇이든그릴수있다.동그라미를그리면나타난축구공을발로뻥!짜잔나타난요정얼굴과댄스!잉크방울속에서만들어진여러가지동물들,두친구는그림과몸짓으로동물들을만들어낸다'),NULL,NULL,NULL);
