INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2590,'PF150931','그때 그 사람','2019.06.27','2019.07.07','대학로스타시티','http://www.kopis.or.kr/upload/pfmPoster/PF_PF150931_190703_143725.gif','공연완료','N','FC000389',NULL,TO_CLOB('줄거리전남여수시의몇가구안되는작은외딴섬에서그물질을하며살고있는부부일선과혜숙은몇년전,사고로바다에빠져죽은딸사월이를가슴에묻은체조용히살아가고있다.한편북한에는먹을식량이없어옥수수알갱이만주워먹는딸봄순이와이런딸을살리기위해열심히그물질하는위철.어느날위철은당의간섭을피해먼바다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2629,'PF150602','제17회 김천국제가족연극제, 아빠가 길을 잃었어요','2019.07.23','2019.07.23','김천시립문화회관','http://www.kopis.or.kr/upload/pfmPoster/PF_PF150602_190702_125724.jpg','공연완료','N','FC001429',NULL,TO_CLOB('줄거리>아빠는왜필요해요?"아빠가왜필요해요?"라는한아이의느닷없는질문을받고그해답을찾아고민하던중이사한집을찾지못해일주일동안방황하는아빠의이야기.집을찾기위한모험을하며자신이어린시절되고싶어했던영웅들을만나게된다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2647,'PF150302','변강쇠 점 찍고 옹녀 [성남]','2019.07.19','2019.07.20','성남아트센터','http://www.kopis.or.kr/upload/pfmPoster/PF_PF150302_190630_175503.PNG','공연완료','N','FC000016',NULL,TO_CLOB('줄거리평안도월경촌에마음곱고인물반반하며몸매도빼어난이름하야,옹녀가산다.문제는팔자!사주에청상과부살이겹겹이라.만나는남편마다해를못넘기고죽어난다.문제는남편뿐만아니라미망인옹녀를탐하거나범하는수많은남자들역시다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2683,'PF149936','대한민국 소극장열전, 무림의 고수','2019.06.30','2019.06.30','한울림소극장','http://www.kopis.or.kr/upload/pfmPoster/PF_PF149936_190626_184625.jpg','공연완료','N','FC000858',NULL,TO_CLOB('줄거리여관이여관답지않고,주인이주인답지않은이상하고묘한기운이흐르는시골여관''강호장''에어느날두남자가찾아온다.과거사람을죽이고살인죄로15년복역후막노동판을전전하며자신이죽인사람의가족들을남몰래돌보고있는한사내와시골여관에서생기는살인사건을소재로작품집필을하기위해조용한여관을찾아투숙한작가.새로운두남자의등장으로''강호장''에는이상한긴장감이흐르기시작한다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2705,'PF149766','팥죽할머니와 호랑이 [구리]','2019.07.20','2019.07.21','롯데백화점 [구리]','http://www.kopis.or.kr/upload/pfmPoster/PF_PF149766_190625_194623.png','공연완료','N','FC000890',NULL,TO_CLOB('줄거리어느산골마을에할머니가홀로밭에팥농사를지으며살고있었어요.어흥!갑자기커다란호랑이가나타나할머니를잡아먹으려고해요.할머니는동짓날팥죽을쑤어먹을때까지만기다려달라고했어요.여름,가을이지나고동짓날이되어호랑이가올때가가까워지자할머니는팥죽을쑤면서눈물을흘렸어요.그런데갑자기할머니앞에알밤,송곳,개똥,맷돌,자라,멍석,지게가차례로와서는팥죽한그릇씩얻어먹고호랑이를물리치도록도와주겠다고해요..과연이들은호랑이를어떻게물리치고할머니를구해주었을까요?'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2720,'PF149113','제17회 김천국제가족연극제, 도망간 내 꼬리','2019.07.21','2019.07.21','김천시립문화회관','http://www.kopis.or.kr/upload/pfmPoster/PF_PF149113_190621_113242.jpg','공연완료','N','FC001429',NULL,TO_CLOB('줄거리>정글의왕사자쭌이와함께떠나는여행동물나라의동물들은모두꼬리가있습니다.거짓말을하거나부모님의말씀을잘듣지않으면꼬리가도망가버린대요.쭌이는장차미래에동물나라를이끌어갈사자예요.하지만부모님말씀을잘듣지않아서꼬리가사라져버렸어요.꼬리가없으니제대로서있기도힘들고,친구들이꼬리가없다는이유로쭌이를싫어해요.비를거리는쭌이는이내정신을차리고사라진꼬리를찾아정글속신기한마을을돌아다니며꼬리를찾아여행을시작해요.과연쭌이의꼬리는어디로간것일까'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (2733,'PF149041','엄마는 오십에 바다를 보았다 [부산]','2019.06.22','2019.06.30','가온아트홀 [부산]','http://www.kopis.or.kr/upload/pfmPoster/PF_PF149041_190619_095749.jpg','공연완료','N','FC000838',NULL,TO_CLOB('줄거리엄마와단둘이살고있는딸이있었습니다.전통적인교육을받고자라남편과자식.가정밖에모르는전형적인주부였던엄마는갑작스러운남편의죽음으로인해깊은상처와외로움을안고있었습니다.그래서엄마는삶의유일한희망이었던에게자신의모든관심을쏟았고,딸은그런엄마가오히려부담스러웠습니다'),NULL,NULL,NULL);
