INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (4806,'PF137619','보잉보잉 [부산]','2017.05.25','2017.07.23','BNK부산은행 조은극장','http://www.kopis.or.kr/upload/pfmPoster/PF_PF137619_170529_132017.jpg','공연완료','N','FC001246',NULL,TO_CLOB('SYNOPSIS매력적인바람둥이조성기이놈은뭔능력인지각기다른개성의여인3명과동시에연애중이다.여자들이모두스튜어디스인관계로스케줄에따라걸리지않고열정적인연애를즐기고있다.그러던어느날,조성기의시골친구순성이가찾아오고하필그날비행일정이꼬이기시작하면서조성기의애인들이집으로하나둘씩찾아온다'),NULL,NULL,NULL);
