INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5202,'PF135240','담벼락','2017.02.14','2017.02.19','청운예술극장(구. 글로브극장)','http://www.kopis.or.kr/upload/pfmPoster/PF_PF135240_170126_113952.gif','공연완료','N','FC000195',NULL,TO_CLOB('줄거리어린시절부모님을여의고기숙학교에서살게된유진과방학마다공부를위해기숙학교를찾아오는소아방학이면고향에내려와부모님이운영하는포도밭에서일을돕는려훈유진과소아가고3이되고,진로에대해고민하던두사람에게담너머로작년에도들려왔던노랫소리가들려온다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5244,'PF134963','산울림 고전극장, 카논-안티고네','2017.02.15','2017.02.26','산울림소극장','http://www.kopis.or.kr/upload/pfmPoster/PF_PF134963_170113_144115.gif','공연완료','N','FC000798',NULL,TO_CLOB('줄거리연극<안티고네>를연습하기위해연출과다섯명의배우들이모인다.현대적인<안티고네>를만들려고작품안팎으로고군분투하는초짜연출.후배들사이에서소외되지않으려고노력하는꼰대경계증크레온.선배와후배사이에껴서방황하는하이몬.까칠한군기반장안티고네.늘생글거리지만알고보면서운한게많은이스메네.그리고눈치없는막내코러스.첫연습날,연출과배우들은처음으로다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5279,'PF134788','그와 그녀의 옷장','2017.01.04','2017.01.05','성미산마을극장 향','http://www.kopis.or.kr/upload/pfmPoster/PF_PF134788_170105_112014.jpg','공연완료','N','FC000138',NULL,TO_CLOB('줄거리에피소드1.호남이의옷장아파트경비원강호남과김영광은들도없는친구사이.그러나사십년지기우정이하루아침에정비옷한별로인해갈라질위기에처한다.일자리와의리사이에서갈등하며속상해하는호남.그러던어느날밤에강호남의앞에원청카바한별이등장하고그는정의감과패기넘혔던지난날의자신의모습을떠움리게되는데'),NULL,NULL,NULL);
