INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5335,'PF134566','모차르트와 모짜렐라의 마술피리 이야기','2017.08.08','2017.08.13','세종문화회관','http://www.kopis.or.kr/upload/pfmPoster/PF_PF134566_170720_181607.jpg','공연완료','N','FC000020',NULL,TO_CLOB('줄거리여러분의마술피리는무엇인가요?모차르트에게는아름다운소리를내는''마술피리''와이야기친구''모짜렐라''가있었습니다.모짜렐라는이야기를들려주는우쿨렐레를가지고있었어요.그런데어느날모짜렐라는그소중한우쿨렐레를잃어버리고말았습니다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5357,'PF134449','산타마을로 간 피터팬','2016.12.10','2016.12.11','부산시민회관','http://www.kopis.or.kr/upload/pfmPoster/PF_PF134449_161205_135231.jpg','공연완료','N','FC000497',NULL,TO_CLOB('줄거리를즐겁게퓨전할수있는효과와어려울수있는융합(컨버전스)에쉽게다가설수있습니다.조금은어려울수있는내용을우리아이들의눈높이에맞춘공연이기에나이가어린아이들도충분히공감할수있도록각색하였습니다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5424,'PF134067','가스등','2016.11.01','2016.11.27','한옥마을아트홀','http://www.kopis.or.kr/upload/pfmPoster/PF_PF134067_161102_111852.jpg','공연완료','N','FC001418',NULL,TO_CLOB('줄거리가끔씩정신을잃고자신이무슨행동을하는지잘알지못하는여주인공은어머니가정신병으로사망한병력을물려받았을까봐두려움에떤다.이런아내를남편은이해하며보살펴주지만아내의상태는더욱심해져만간다'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5441,'PF133980','프랭키와 친구들 [광주]','2016.11.06','2016.11.06','5.18기념문화센터','http://www.kopis.or.kr/upload/pfmPoster/PF_PF133980_161026_131314.jpg','공연완료','N','FC000420',NULL,TO_CLOB('줄거리프랭키와친구들은파란하늘,환구름,푸른풀밭의아름다운동화마을에서살고있어요.저녁으로나온콩요리가싫은프랭키는마을로나갔어요.마을에서만난마녀는기름진음식으로프랭키를유혹했어요.배가고픈프랭키는유혹을이기지못하고친구들몰래마녀가준음식을먹었어요그날저녁프랭키는배가야파요.그리곤아주아주지독한방귀가나왔어요.프랭키와친구들은지독한방귀의원인을찾아보기로했어요.과연프랭키와친구들은원인을찾을수있을까요?'),NULL,NULL,NULL);
INSERT INTO RECOPAY.PERFORM (PRF_UID,PRF_ID,PRF_NAME,PRF_FROM,PRF_TO,PRF_FCLTYNM,PRF_POSTER,PRF_STATE,PRF_OPENRUN,TH_UID,PRF_AVGSC,PRF_SUMMARY,PRF_RUNDAY,PRF_RUNTIME,PRF_TICKETPRICE) VALUES (5449,'PF133948','세종,인재를 뽑다','2016.10.15','2016.11.13','종로아이들극장','http://www.kopis.or.kr/upload/pfmPoster/PF_PF133948_161024_140313.jpg','공연완료','N','FC001435',NULL,TO_CLOB('줄거리새로운인재를등용하기위해과거시험장으로행차하는세종대왕,갑자기신문고가울리고한소녀가탐관오리에게속아억울하게논과밭을빼앗기고옥에갇힌아비를구해달라며울고있다.세종은지혜로운방법으로탐관오리를벌주고어려운한문을몰라억울한일을당하는백성을위해우리고유의문자를만들기로결심하고과거시험을열어훌륭한인재를선발하는데'),NULL,NULL,NULL);
